$_SP_DEFAULT_DATA[] = "INSERT INTO {$_TABLES['staticpage']} (`sp_id`, `sp_status`, `sp_uid`, `sp_title`, `sp_content`, `sp_hits`, `sp_date`, `sp_format`, `sp_onmenu`, `sp_label`, `commentcode`, `owner_id`, `group_id`, `perm_owner`, `perm_group`, `perm_members`, `perm_anon`, `sp_centerblock`, `sp_help`, `sp_tid`, `sp_where`, `sp_php`, `sp_nf`, `sp_inblock`, `postmode`, `sp_search`) VALUES('slide_jquery', 1, 2, 'jQuery', '<p><img alt=\"\" src=\"xxxSITEURLxxx/assets/image/defaultcontent/jQuery-Logo.png\" style=\"width: 278px; height: 71px; float: right;\" /></p>\r\n\r\n<h3><em><span style=\"margin-top: 10px; font-size: large\">What is jQuery?</span></em></h3>\r\n\r\n<p><a href=\"http://jquery.com/\"><u>jQuery&nbsp;</u></a>is a powerful, yet compact, object-oriented javascript framework that is fully integrated into the <em><strong>glFusion CMS</strong></em>.</p>\r\n\r\n<p>Their&nbsp;<a href=\"http://api.jquery.com/\" target=\"_blank\">well-documented API</a> allows devs &amp; site admins to easily create a wide range of dynamic enhancements that extend the functionality in a <em><strong>glFusion</strong></em> powered site.</p>\r\n', 1, '2014-05-10 06:04:05', 'allblocks', 0, '', -1, 2, 14, 3, 2, 2, 2, 0, '', 'none', 1, 0, 0, 0, '', 0);";
$_SP_DEFAULT_DATA[] = "INSERT INTO {$_TABLES['staticpage']} (`sp_id`, `sp_status`, `sp_uid`, `sp_title`, `sp_content`, `sp_hits`, `sp_date`, `sp_format`, `sp_onmenu`, `sp_label`, `commentcode`, `owner_id`, `group_id`, `perm_owner`, `perm_group`, `perm_members`, `perm_anon`, `sp_centerblock`, `sp_help`, `sp_tid`, `sp_where`, `sp_php`, `sp_nf`, `sp_inblock`, `postmode`, `sp_search`) VALUES('slide_cachetech', 1, 2, 'Cache Technology', '<p><img align=\"right\" alt=\"Cache\" height=\"128\" hspace=\"5\" src=\"xxxSITEURLxxx/assets/image/defaultcontent/cache.png\" width=\"128\" /></p>\r\n\r\n<h3><em><span style=\"font-size: large;\">Template Caching Technology</span></em></h3>\r\n\r\n<p><em><strong>glFusion</strong></em> includes powerful template caching technology that streamlines page rendering &amp; reduces the load on your web server.</p>\r\n\r\n<p>It also eliminates redundant template files, which allows for streamlined development, customization &amp; delivery!</p>\r\n', 1, '2014-05-10 06:02:44', 'allblocks', 0, '', -1, 2, 14, 3, 2, 2, 2, 0, '', 'none', 1, 0, 0, 0, '', 0);";
$_SP_DEFAULT_DATA[] = "INSERT INTO {$_TABLES['staticpage']} (`sp_id`, `sp_status`, `sp_uid`, `sp_title`, `sp_content`, `sp_hits`, `sp_date`, `sp_format`, `sp_onmenu`, `sp_label`, `commentcode`, `owner_id`, `group_id`, `perm_owner`, `perm_group`, `perm_members`, `perm_anon`, `sp_centerblock`, `sp_help`, `sp_tid`, `sp_where`, `sp_php`, `sp_nf`, `sp_inblock`, `postmode`, `sp_search`) VALUES('slide_whatsnew', 1, 2, 'What''s New', '<p><img align=\"left\" alt=\"whats new\" height=\"135\" hspace=\"18\" src=\"xxxSITEURLxxx/assets/image/defaultcontent/whatsnew.png\" vspace=\"5\" width=\"135\" /></p>\r\n\r\n<h3><span style=\"font-size: large; padding-top: 10px;\"><em>What&#39;s New in glFusion?</em></span></h3>\r\n\r\n<p><img align=\"top\" alt=\"\" height=\"16\" hspace=\"5\" src=\"xxxSITEURLxxx/layout/cms/images/bullet-star.png\" width=\"16\" />New UIKIT based responsive layout!<br />\r\n<img align=\"top\" alt=\"\" height=\"16\" hspace=\"5\" src=\"xxxSITEURLxxx/layout/cms/images/bullet-star.png\" width=\"16\" />Improved Spam / Bot protection!<br />\r\n<img align=\"top\" alt=\"\" height=\"16\" hspace=\"5\" src=\"xxxSITEURLxxx/layout/cms/images/bullet-star.png\" width=\"16\" />Responsive video support!<br />\r\n<img align=\"top\" alt=\"\" height=\"16\" hspace=\"5\" src=\"xxxSITEURLxxx/layout/cms/images/bullet-star.png\" width=\"16\" />Updated Image Manager for CKEditor!</p>\r\n\r\n<p>&nbsp;And more! Visit <a href=\"https://www.glfusion.org/wiki/doku.php?id=glfusion:whatsnew\" target=\"_blank\">www.glfusion.org</a> for a detailed list!</p>\r\n', 0, '2015-09-11 20:24:36', 'allblocks', 0, '', -1, 2, 14, 3, 2, 2, 2, 0, '', 'none', 1, 0, 0, 0, '', 0);";
$_SP_DEFAULT_DATA[] = "INSERT INTO {$_TABLES['staticpage']} (`sp_id`, `sp_status`, `sp_uid`, `sp_title`, `sp_content`, `sp_hits`, `sp_date`, `sp_format`, `sp_onmenu`, `sp_label`, `commentcode`, `owner_id`, `group_id`, `perm_owner`, `perm_group`, `perm_members`, `perm_anon`, `sp_centerblock`, `sp_help`, `sp_tid`, `sp_where`, `sp_php`, `sp_nf`, `sp_inblock`, `postmode`, `sp_search`) VALUES('slide_integratedplugins', 1, 2, 'Integrated Plugins', '<p><img align=\"left\" alt=\"whats new\" height=\"128\" hspace=\"5\" src=\"xxxSITEURLxxx/assets/image/defaultcontent/plugins.png\" vspace=\"10\" width=\"128\" /></p>\r\n\r\n<h3><span style=\"font-size: large;\"><em>Integrated Best of Class Plugins</em></span></h3>\r\n\r\n<p>&nbsp; <em><strong>glFusion</strong></em> comes with the best plugins pre-installed:</p>\r\n\r\n<p>&nbsp;<img align=\"top\" alt=\"\" height=\"16\" hspace=\"5\" src=\"xxxSITEURLxxx/layout/cms/images/check.png\" width=\"16\" /><strong>Captcha</strong> - only accept human generated content!<br />\r\n<img align=\"top\" alt=\"\" height=\"16\" hspace=\"5\" src=\"xxxSITEURLxxx/layout/cms/images/check.png\" width=\"16\" /><strong>Forum</strong> - enable online discussion &amp; collaboration!<br />\r\n<img align=\"top\" alt=\"\" height=\"16\" hspace=\"5\" src=\"xxxSITEURLxxx/layout/cms/images/check.png\" width=\"16\" /><strong>File Mgmt</strong> - manage downloads availabe to visitors!<br />\r\n<img align=\"top\" alt=\"\" height=\"16\" hspace=\"5\" src=\"xxxSITEURLxxx/layout/cms/images/check.png\" width=\"16\" /><strong>Media Gallery</strong> - full-featured media management!</p>\r\n', 1, '2015-09-11 20:25:29', 'allblocks', 0, '', -1, 2, 14, 3, 2, 2, 2, 0, '', 'none', 1, 0, 0, 0, '', 0);";
$_SP_DEFAULT_DATA[] = "INSERT INTO {$_TABLES['staticpage']} (`sp_id`, `sp_status`, `sp_uid`, `sp_title`, `sp_content`, `sp_hits`, `sp_date`, `sp_format`, `sp_onmenu`, `sp_label`, `commentcode`, `owner_id`, `group_id`, `perm_owner`, `perm_group`, `perm_members`, `perm_anon`, `sp_centerblock`, `sp_help`, `sp_tid`, `sp_where`, `sp_php`, `sp_nf`, `sp_inblock`, `postmode`, `sp_search`) VALUES('slide_widgets', 1, 2, 'Widgets', '<p><img align=\"left\" alt=\"Widgets\" height=\"128\" hspace=\"5\" src=\"xxxSITEURLxxx/assets/image/defaultcontent/widgets.png\" vspace=\"10\" width=\"128\" /></p>\r\n\r\n<h3><em><span style=\"font-size: large\">All Your Favorite Widgets</span></em></h3>\r\n\r\n<p>Building on the foundation of <a href=\"http://jquery.com/\">jQuery</a>, and coupled with the power of <em><strong>glFusion&#39;s</strong></em> native block &amp; staticpage editors, we&#39;ve included some pre-configured widgets to enhance the functionality of your site!</p>\r\n\r\n<p>For a complete list of widgets,&nbsp;and how to configure them, visit the <a href=\"https://www.glfusion.org/wiki/glfusion:widgets\" target=\"_blank\">glFusion documentation</a>.</p>\r\n', 2, '2014-05-10 05:58:46', 'allblocks', 0, '', -1, 2, 14, 3, 2, 2, 2, 0, '', 'none', 1, 0, 0, 0, '', 0);";
$_SP_DEFAULT_DATA[] = "INSERT INTO {$_TABLES['staticpage']} (`sp_id`, `sp_status`, `sp_uid`, `sp_title`, `sp_content`, `sp_hits`, `sp_date`, `sp_format`, `sp_onmenu`, `sp_label`, `commentcode`, `owner_id`, `group_id`, `perm_owner`, `perm_group`, `perm_members`, `perm_anon`, `sp_centerblock`, `sp_help`, `sp_tid`, `sp_where`, `sp_php`, `sp_nf`, `sp_inblock`, `postmode`, `sp_search`) VALUES('rotator', 1, 2, 'Rotator Example', '// this staticpage needs to have PHP set to execute PHP below\r\n// use lib-widgets.php\r\nUSES_lib_widgets();\r\n\r\n\$sliderData = array(\r\n''images'' => array(\r\n  array(\r\n    ''image'' => ''%site_url%/assets/image/defaultcontent/moorotator1.jpg'',\r\n    ''caption'' => ''Welcome to the glFusion Revolution!<br>Learn more about the glFusion development philosophy today!'',\r\n    ''link''  => ''https://www.glfusion.org/wiki/glfusion:getinvolved''\r\n  ),\r\n  array(\r\n    ''image'' => ''%site_url%/assets/image/defaultcontent/moorotator2.jpg'',\r\n    ''caption'' => ''<b>Widgets</b><br>Sliders, Tab Sliders, Spring Menus and more!'',\r\n    ''link'' => ''https://www.glfusion.org/wiki/glfusion:widgets'',\r\n  ),\r\n  array(\r\n    ''image'' => ''%site_url%/assets/image/defaultcontent/moorotator3.jpg'',\r\n    ''caption'' => ''<b>Language Localization</b><br>Support for a multi-lingual site right out of the box!'',\r\n    ''link'' => ''https://www.glfusion.org/wiki/glfusion:language'',\r\n  ),\r\n  array(\r\n    ''image'' => ''%site_url%/assets/image/defaultcontent/moorotator4.jpg'',\r\n    ''caption'' => ''<b>Bugs Belong in Nature, not glFusion</b><br>Found a bug? Visit our Tracker and submit a report!'',\r\n    ''link'' => ''https://github.com/glFusion/glfusion/issues'',\r\n  ),\r\n  array(\r\n    ''image'' => ''%site_url%/assets/image/defaultcontent/moorotator5.jpg'',\r\n    ''caption'' => ''<b>Remote Social Authentication</b><br>Login to glFusion using your favorite social networking services.'',\r\n    ''link'' => ''https://www.glfusion.org/wiki/glfusion:remoteauth'',\r\n  ),\r\n\r\n  /* repeat the array as needed for more slides */\r\n\r\n),\r\n  \r\n''options'' => array(\r\n    ''effect'' =>  ''random'',             // Specify sets like'' =>  ''fold,fade,sliceDown'' \r\n    ''slices'' =>  15,                   // For slice animations \r\n    ''boxCols'' =>  8,                   // For box animations \r\n    ''boxRows'' =>  4,                   // For box animations \r\n    ''animSpeed'' =>  500,               // Slide transition speed \r\n    ''pauseTime'' =>  3000,              // How long each slide will show \r\n    ''startSlide'' =>  0,                // Set starting Slide (0 index) \r\n    ''directionNav'' =>  true,           // Next & Prev navigation \r\n    ''controlNav'' =>  true,             // 1,2,3... navigation \r\n    ''controlNavThumbs'' =>  false,      // Use thumbnails for Control Nav \r\n    ''pauseOnHover'' =>  true,           // Stop animation while hovering \r\n    ''manualAdvance'' =>  false,         // Force manual transitions \r\n    ''prevText'' =>  ''Prev'',             // Prev directionNav text \r\n    ''nextText'' =>  ''Next'',             // Next directionNav text \r\n    ''randomStart'' =>  false            // Start on a random slide \r\n),\r\n\r\n// you can specify custom template name\r\n''template'' => ''slider.thtml'',\r\n);\r\n\r\nreturn WIDGET_slider(\$sliderData);\r\n', 753, '2016-05-12 12:37:40', 'leftblocks', 0, '', -1, 2, 14, 3, 2, 2, 2, 1, '', 'none', 1, 1, 0, 0, '', 0);";
